mapper.select:
SELECT * from loanRecordWithBookInfoVIEW

	WHERE ano = #{ano} 


	WHERE bisbn = #{bisbn}


	WHERE btitle like CONCAT('%', #{btitle}, '%')


	AND lcreatedAt BETWEEN #{lcreatedAtStart} AND #{lcreatedAtEnd}

ORDER BY lno DESC

mapper.update:
UPDATE loanRecordTBL
SET lreturnedAt = #{lreturnedAt}
WHERE lno =  #{lno}
